}

export const microCategories: MicroCategory[] = [
  {
    id: 'impulse',
    label: '–ò–º–ø—É–ª—å—Å',
    icon: '‚ö°',
    description: '–ë—ã—Å—Ç—Ä—ã–π —Ç–æ–ª—á–æ–∫, —á—Ç–æ–±—ã –ø—Ä–æ—Å–Ω—É—Ç—å—Å—è',
    actions: [
      '–°–¥–µ–ª–∞–π 15 –ø—Ä–∏—Å–µ–¥–∞–Ω–∏–π –±–µ–∑ –º—É–∑—ã–∫–∏',
      '–ù–∞–ø–∏—à–∏ –¥—Ä—É–≥—É –æ–¥–Ω—É —á–µ—Å—Ç–Ω—É—é —Ñ—Ä–∞–∑—É –∏ –æ—Ç–ø—Ä–∞–≤—å',
      '–£–º–æ–π—Å—è —Ö–æ–ª–æ–¥–Ω–æ–π –≤–æ–¥–æ–π –∏ —Å–¥–µ–ª–∞–π –≥–ª—É–±–æ–∫–∏–π –≤–¥–æ—Ö',
      '–û—Ç–∫—Ä–æ–π –æ–∫–Ω–æ –∏ –ø–æ–¥—ã—à–∏ 60 —Å–µ–∫—É–Ω–¥, –≥–ª—è–¥—è –≤–¥–∞–ª—å',
      '–°–¥–µ–ª–∞–π 20 –æ—Ç–∂–∏–º–∞–Ω–∏–π, –µ—Å–ª–∏ –Ω–µ –º–æ–∂–µ—à—å ‚Äî —Å—Ç–æ–ª—å–∫–æ, —Å–∫–æ–ª—å–∫–æ –≤—ã–π–¥–µ—Ç'
    ]
  },
  {
    id: 'switch',
    label: '–ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ',
    icon: 'üß†',
    description: '–ú–µ–Ω—è–µ–º –∫–æ–Ω—Ç–µ–∫—Å—Ç, —á—Ç–æ–±—ã –º–æ–∑–≥ –æ–∂–∏–ª',
    actions: [
      '–°–º–µ–Ω–∏—Ç—å –ø–ª–µ–π–ª–∏—Å—Ç: –≤–∫–ª—é—á–∏ –∂–∞–Ω—Ä, –∫–æ—Ç–æ—Ä—ã–π —Ä–µ–¥–∫–æ —Å–ª—É—à–∞–µ—à—å',
      '–í—ã–π–¥–∏ –Ω–∞ —É–ª–∏—Ü—É –∏ –¥–æ–π–¥–∏ –¥–æ –±–ª–∏–∂–∞–π—à–µ–≥–æ —É–≥–ª–∞, —Å—á–∏—Ç–∞—è —à–∞–≥–∏',
      '–°–¥–µ–ª–∞–π 3 —Ñ–æ—Ç–æ –Ω–µ–æ–±—ã—á–Ω—ã—Ö –¥–µ—Ç–∞–ª–µ–π –≤–æ–∫—Ä—É–≥',
      '–ó–∞–ø–∏—à–∏ –≥–æ–ª–æ—Å–æ–º –æ–¥–Ω—É –∏–¥–µ—é, —á—Ç–æ —Å–¥–µ–ª–∞—Ç—å —Å–µ–≥–æ–¥–Ω—è',
      '–°—è–¥—å –∏ 2 –º–∏–Ω—É—Ç—ã —Å–º–æ—Ç—Ä–∏ –Ω–∞ –ø–æ—Ç–æ–ª–æ–∫, –±–µ–∑ —Ç–µ–ª–µ—Ñ–æ–Ω–∞'
    ]
  },
  {
    id: 'wolfstep',
    label: '–í–æ–ª—á–∏–π —à–∞–≥',
    icon: 'üê∫',
    description: '–î–≤–∏–∂–µ–Ω–∏–µ –≤–ø–µ—Ä–µ–¥ –≤ —Å–≤–æ–π —Ä–∏—Ç–º',
    actions: [
      '–ù–∞–±–µ—Ä–∏ –≤–æ–¥—É, —Å–¥–µ–ª–∞–π –ø–∞—Ä—É –≥–ª–æ—Ç–∫–æ–≤ –∏ –∑–∞–∫—Ä–æ–π –∑–∞–¥–∞—á—É, —á—Ç–æ –æ—Ç–∫–ª–∞–¥—ã–≤–∞–ª 5 –º–∏–Ω—É—Ç',
      '–£–±–µ—Ä–∏ –æ–¥–Ω—É –≤–µ—â—å —Å–æ —Å—Ç–æ–ª–∞ –∏–ª–∏ –∏–∑ –∫–æ–º–Ω–∞—Ç—ã',
      '–°–¥–µ–ª–∞–π –ø–ª–∞–Ω –Ω–∞ –±–ª–∏–∂–∞–π—à–∏–µ 30 –º–∏–Ω—É—Ç –∏–∑ 3 –ø—É–Ω–∫—Ç–æ–≤',
      '–ó–∞–ø–∏—à–∏ —Ü–µ–ª—å –Ω–∞ –¥–µ–Ω—å –æ–¥–Ω–∏–º –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏–µ–º –∏ –ø–æ—Å—Ç–∞–≤—å —Å–µ–±–µ –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏–µ',
      '–ù–∞–¥–µ–Ω—å –æ–±—É–≤—å –∏ –≤—ã–π–¥–∏ –ø—Ä–æ–π—Ç–∏—Å—å –≤–æ–∫—Ä—É–≥ –¥–æ–º–∞ –∏–ª–∏ –ø–æ–¥—ä–µ–∑–¥–∞'
    ]
  }
];
<template>
  <section class="tod-page">
    <div class="tod-container">
      <div v-if="!gameStarted" class="tod-intro">
        <h1 class="tod-title">–ú–∏–∫—Ä–æ –¥–µ–π—Å—Ç–≤–∏–µ</h1>
        <p class="tod-subtitle">1‚Äì5 –º–∏–Ω—É—Ç, —á—Ç–æ–±—ã —Å–¥–≤–∏–Ω—É—Ç—å—Å—è —Å –º–µ—Å—Ç–∞. –ñ–º–∏ –∏ –¥–µ–π—Å—Ç–≤—É–π –ø—Ä—è–º–æ —Å–µ–π—á–∞—Å.</p>
        <button class="tod-btn tod-btn-primary" @click="startGame">–î–∞—Ç—å –¥–µ–π—Å—Ç–≤–∏–µ</button>
        <p class="tod-hint">–í—ã–±–∏—Ä–∞–µ–º —Å–ª—É—á–∞–π–Ω–æ–µ –¥–µ–π—Å—Ç–≤–∏–µ —Å—Ä–∞–∑—É –ø–æ—Å–ª–µ —Å—Ç–∞—Ä—Ç–∞.</p>
      </div>

      <div v-else class="tod-game">
        <div class="tod-modes">
          <button
            v-for="cat in categories"
            :key="cat.id"
            class="tod-chip"
            :class="{ 'tod-chip-active': activeCategory === cat.id }"
            @click="() => changeCategory(cat.id)"
          >
            <span class="tod-chip-label">
              <span class="tod-chip-icon">{{ cat.icon }}</span>
              {{ cat.label }}
            </span>
            <span class="tod-chip-desc">{{ cat.description }}</span>
          </button>
        </div>

        <div class="tod-card">
          <div class="tod-label">
            <span>–º–∏–∫—Ä–æ –¥–µ–π—Å—Ç–≤–∏–µ</span>,
            <span>{{ currentCategoryLabel }}</span>
          </div>
          <div class="tod-question-text" :class="{ 'tod-question-animate': isAnimating }">
            {{ currentAction }}
          </div>
          <p class="tod-wolf-hint" v-if="feedback">
            <span class="tod-wolf-hint-strong">{{ feedback }}</span>
          </p>
        </div>

        <div class="tod-actions">
          <div class="tod-buttons">
            <button class="tod-btn tod-btn-ghost" @click="nextAction">–î—Ä—É–≥–æ–µ</button>
            <button class="tod-btn tod-btn-primary" @click="completeAction">–°–¥–µ–ª–∞–ª</button>
          </div>
        </div>
      </div>
    </div>
    <SocialPopup :visible="showPopup" :payload="popupPayload" @close="showPopup = false" />
  </section>
</template>

<script setup>
import { computed, ref } from 'vue'
import SocialPopup from '@/components/ui/SocialPopup.vue'
import { microCategories } from '@/data/microActions'

const categories = microCategories
const gameStarted = ref(false)
const activeCategory = ref(categories[0].id)
const currentAction = ref('')
const lastIndex = ref({ impulse: -1, switch: -1, wolfstep: -1 })
const isAnimating = ref(false)
const feedback = ref('')
const pulls = ref(0)
const showPopup = ref(false)
const popupIndex = ref(0)

const currentCategoryLabel = computed(() => {
  const item = categories.find((c) => c.id === activeCategory.value)
  return item ? item.label : activeCategory.value
})

const socials = [
  {
    title: '–ü–æ–¥–ø–∏—à–∏—Å—å –Ω–∞ Telegram',
    text: '–ö—É—á–∞ –º–µ–º–æ–≤, –≤—Å—ë —Å–∞–º–æ–µ —Å–≤–µ–∂–µ–µ —Ç—É—Ç',
    cta: '–ü–µ—Ä–µ–π—Ç–∏ –≤ –ª–æ–≥–æ–≤–æ',
    link: 'https://t.me/neural_wise_wolf',
    emoji: 'üê∫'
  },
  {
    title: '–ó–∞–ª–µ—Ç–∞–π –≤ Instagram',
    text: '–°–∞–º–æ–µ –ø–µ—Ä–≤–æ–µ –∏ –±–æ–ª—å—à–æ–µ —Å–æ–æ–±—â–µ—Å—Ç–≤–æ, –º–Ω–æ–≥–æ –º–µ–º–æ–≤ —Å –≤–æ–ª–∫–∞–º–∏',
    cta: '–û—Ç–∫—Ä—ã—Ç—å Instagram',
    link: 'https://instagram.com/neural_wise_wolf/',
    emoji: 'üì∏'
  },
  {
    title: 'TikTok –í–æ–ª–∫–∞',
    text: '–ú–µ–º—ã, —Å—Ç—Ä–∏–º—ã –∏ –º–Ω–æ–≥–æ –≤–æ–ª–∫–æ–≤',
    cta: '–°–º–æ—Ç—Ä–µ—Ç—å TikTok',
    link: 'https://www.tiktok.com/@neural_wolf',
    emoji: 'üé•'
  },
  {
    title: 'YouTube –∫–∞–Ω–∞–ª',
    text: '–®–æ—Ä—Ç—Å—ã –∏ –¥–ª–∏–Ω–Ω—ã–µ –≤–∏–¥–æ—Å—ã —Å –≤–æ–ª–∫–∞–º–∏',
    cta: '–û—Ç–∫—Ä—ã—Ç—å YouTube',
    link: 'https://www.youtube.com/@neural_wolf',
    emoji: '‚ñ∂Ô∏è'
  }
]

const popupPayload = computed(() => socials[popupIndex.value % socials.length])

function resetAnimation() {
  isAnimating.value = false
  requestAnimationFrame(() => {
    isAnimating.value = true
  })
}

// –ü–æ–ª—É—á–∞–µ–º —Å–ª—É—á–∞–π–Ω–æ–µ –¥–µ–π—Å—Ç–≤–∏–µ, —Å—Ç–∞—Ä–∞–µ–º—Å—è –Ω–µ –ø–æ–≤—Ç–æ—Ä—è—Ç—å –ø–æ–¥—Ä—è–¥ –≤ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏
function pickAction(catId) {
  const cat = categories.find((c) => c.id === catId)
  if (!cat || !cat.actions.length) return ''
  const last = lastIndex.value[catId]
  let idx = Math.floor(Math.random() * cat.actions.length)
  if (cat.actions.length > 1 && idx === last) {
    idx = (idx + 1) % cat.actions.length
  }
  lastIndex.value[catId] = idx
  return cat.actions[idx]
}

function showAction(catId) {
  const action = pickAction(catId)
  currentAction.value = action
  feedback.value = ''
  resetAnimation()
  pulls.value += 1
  if (pulls.value % 5 === 0) {
    popupIndex.value += 1
    showPopup.value = true
  }
}

function startGame() {
  gameStarted.value = true
  showAction(activeCategory.value)
}

function changeCategory(catId) {
  activeCategory.value = catId
  showAction(catId)
}

function nextAction() {
  showAction(activeCategory.value)
}

function completeAction() {
  feedback.value = '–∞—É—Ñ, –ø–æ—à–ª–æ –¥–≤–∏–∂–µ–Ω–∏–µ'
  setTimeout(() => {
    feedback.value = ''
  }, 1000)
  showAction(activeCategory.value)
}
</script>

